import { useState } from 'react';
import * as React from 'react';
import {
  View,
  Text,
  TextInput,
  StyleSheet,
  Platform,
} from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { Stepper } from './../../components/Stepper';
import * as Animatable from 'react-native-animatable';
import { Button } from './../../components/Button';

const title = 'Create a Password';
const description = `Use at least one number and one special character:`;
const step = 3;

export const AuthPwdScreen = () => {
  const navigation = useNavigation();
  const [pwd, setPwd] = useState('');

  const onChange = (event, selectedPwd) => {
    const currentPwd = selectedPwd || pwd;
    setShow(Platform.OS === 'ios');
    setPwd(currentPwd);
  };

  const [pwdMatch, setPwdMatch] = React.useState('');

  return (
    <View style={styles.container}>
      <Animatable.View
        animation="fadeIn"
        delay={500}
        duration={1000}
        style={styles.stepper}
      >
        <Stepper activeStep={step} />
      </Animatable.View>
      <Text style={styles.title}>{title}</Text>
      <Text style={styles.description}>{description}</Text>

      <Animatable.View
        animation="fadeInLeft"
        delay={Animations.SPEED_FAST}
        duration={Animations.SPEED_FAST}
        style={styles.inputWrapper}
      >
        <Text style={styles.label}>Password:</Text>
        <TextInput
          placeholder="Password"
          value={pwd}
          onChangeText={setPwd}
          style={styles.input}
        />
      </Animatable.View>

      <Animatable.View
        animation="fadeInRight"
        delay={Animations.SPEED_MED}
        duration={Animations.SPEED_FAST}
        style={styles.inputWrapper}
      >
        <Text style={styles.label}>Re-type Password:</Text>
        <TextInput
          placeholder="Re-type password"
          value={pwdMatch}
          onChangeText={setPwdMatch}
          style={styles.input}
        />
      </Animatable.View>

      <Animatable.View
        animation="fadeInUp"
        delay={Animations.SPEED_LONG}
        duration={Animations.SPEED_MED}
      >
        {/* <Button
          buttonText="Create Account"
          onPress={() => navigation.navigate('HomeScreen')}
          type="solid-green"
        /> */}
        <Button
          arrowDirection="right"
          buttonText="Submit"
          onPress={() => navigation.navigate('HomeScreen')}
          type="solid-green"
          style={styles.resultButton}
        />
      </Animatable.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    display: 'flex',
    flexDirection: 'column',
    flex: 1,
    justifyContent: 'space-around',
    width: '100%',
    paddingHorizontal: 30,
    backgroundColor: Colors.WHITE,
  },
  stepper: {
    height: '25%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    //backgroundColor: '#ffcc00',
  },
  textWrapper: {
    textAlign: 'center',
    //backgroundColor: 'blue',
  },
  title: {
    fontFamily: Fonts.FONT_FACE_TITLE,
    fontSize: Fonts.FONT_SIZE_TITLE,
    textAlign: 'center',
    color: Colors.NAVY,
    lineHeight: Fonts.FONT_SIZE_TITLE,
    //backgroundColor: '#ffcc00',
  },
  description: {
    marginVertical: 6,
    fontFamily: Fonts.FONT_FACE_DESCRIPTION,
    fontSize: Fonts.FONT_SIZE_CAPTION,
    textAlign: 'center',
    color: Colors.NAVY,
    lineHeight: Fonts.FONT_SIZE_DESCRIPTION * 1.5,
    //backgroundColor: 'green',
  },
  inputWrapper: {
    marginBottom: 16,
    //backgroundColor: 'red',
  },
  label: {
    fontFamily: Fonts.FONT_FACE_TITLE,
    fontSize: Fonts.FONT_SIZE_CAPTION,
    color: Colors.NAVY,
    marginVertical: 5,
    height: 20,
  },
  input: {
    fontFamily: Fonts.FONT_FACE_DESCRIPTION,
    fontSize: Fonts.FONT_SIZE_DESCRIPTION,
    color: Colors.NAVY,
    borderRadius: 8,
    height: Dimensions.FORM_HEIGHT,
    borderWidth: 1,
    borderColor: Colors.NAVY,
    minWidth: 200,
    paddingHorizontal: 10,
  },
});
