import { StyleSheet, Text, View } from 'react-native';
import { useWindowDimensions } from 'react-native';
import * as Animatable from 'react-native-animatable';
import { StepperDevice } from '../../components/StepperDevice';
import { BasicsShared } from '../../components/Basics/BasicsShared';
import { NextPrevious } from '../../components/NextPrevious';

const step = 1;
const title = 'Package';
const description = {
  html: `<ul><li>Contains electrodes</li><li>Delivers therapy to nerves in your wrist</li><li>Designed for either right- or left-handed use</li><li>Needs to be replaced to maintain effective therapy delivery</li></ul>`,
};
const bgImage = require('./../../../assets/images/device/packaging.jpg'),
  imageHeight = Dimensions.SETUP_HEIGHT * 1,
  type="outline-green"
hasArrows = true,
buttonPreviousText = 'Device',
buttonPreviousLink = 'SetupScreen',
buttonNextText = 'Stimulator',
buttonNextLink = 'Basics_2_Screen';

export const Basics_1_Screen = () => {
  const { width } = useWindowDimensions();
  return (
    <View style={styles.container}>
      <Animatable.View
        animation="fadeIn"
        delay={500}
        duration={1000}
        style={styles.stepper}
      >
        <StepperDevice activeStep={step} />
      </Animatable.View>
      <Text style={styles.title}>{title}</Text>
      <BasicsShared
        description={description}
        label1Text="Base Station"
        label2Text="Manuals"
        label3Text="Stimulator"
        label4Text="Band"
        label1Animation="fadeInLeft"
        label2Animation="fadeInRight"
        label3Animation="fadeInLeft"
        label4Animation="fadeInRight"
        pointer1Horiz={Dimensions.SETUP_WIDTH * 0.3}
        pointer2Horiz={Dimensions.SETUP_WIDTH * 0.7}
        pointer3Horiz={Dimensions.SETUP_WIDTH * 0.3}
        pointer4Horiz={Dimensions.SETUP_WIDTH * 0.58}
        pointer1Vert={(Dimensions.SETUP_HEIGHT / 2) * 0.1}
        pointer2Vert={(Dimensions.SETUP_HEIGHT / 2) * 0.42}
        pointer3Vert={(Dimensions.SETUP_HEIGHT / 2) * 0.4}
        pointer4Vert={(Dimensions.SETUP_HEIGHT / 2) * 0.25}
        label1Horiz={Dimensions.SETUP_WIDTH * 0.05}
        label2Horiz={Dimensions.SETUP_WIDTH * 0.7}
        label3Horiz={Dimensions.SETUP_WIDTH * 0.1}
        label4Horiz={Dimensions.SETUP_WIDTH * 0.6}
        label1Delay={300}
        label2Delay={900}
        label3Delay={1200}
        label4Delay={600}
        image={bgImage}
        imageHeight={imageHeight}
      />
      <NextPrevious
        type={type}
        hasArrows={hasArrows}
        buttonNextText={buttonNextText}
        buttonNextLink={buttonNextLink}
        buttonPreviousText={buttonPreviousText}
        buttonPreviousLink={buttonPreviousLink}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    display: 'flex',
    flexDirection: 'column',
    flex: 1,
    justifyContent: 'space-around',
    width: '100%',
    paddingHorizontal: 10,
    backgroundColor: Colors.WHITE,
    //backgroundColor: 'red',
  },
  title: {
    fontFamily: Fonts.FONT_FACE_TITLE,
    fontSize: Fonts.FONT_SIZE_TITLE,
    color: Colors.NAVY,
    textAlign: 'center',
    //backgroundColor: 'red',
  },
  description: {
    height: 'auto',
    fontFamily: Fonts.FONT_FACE_DESCRIPTION,
    fontSize: Fonts.FONT_SIZE_DESCRIPTION,
    color: Colors.NAVY,
    textAlign: 'center',
    lineHeight: Fonts.FONT_SIZE_DESCRIPTION * 1.5,
    //backgroundColor: 'red',
    //backgroundColor: 'gold',
  },
});
