import { View, StyleSheet } from 'react-native';
import { Animated } from './Animated';
import './customAnimations';

const globalDelay = 0.2;

const baseAnimation = 'zoomIn',
  baseDelay = 500,
  baseDuration = 1000,
  baseEasing = 'ease-in-out',
  baseIterationCount = 1,
  baseSrc = require('./../../../assets/images/animation/circle_purple.png'),
  baseStyles = 'circle';

const waveformAnimation = 'waveform',
  waveformDelay = 0,
  waveformDuration = 5000,
  waveformEasing = 'linear',
  waveformIterationCount = 'infinite',
  waveformSrc = require('./../../../assets/images/animation/waveform_white.png'),
  waveformStyles = 'styles.waveform';

export const Waveform = () => {
  return (
    <View style={styles.animationWrapper}>
      <Animated
        animation={baseAnimation}
        delay={globalDelay + baseDelay}
        duration={baseDuration}
        easing={baseEasing}
        iterationCount={baseIterationCount}
        source={baseSrc}
        styles={baseStyles}
      />
      <Animated
        animation={waveformAnimation}
        delay={globalDelay + waveformDelay}
        duration={waveformDuration}
        easing={waveformEasing}
        iterationCount={waveformIterationCount}
        source={waveformSrc}
        style={{ waveformStyles }}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  animationWrapper: {
    position: 'relative',
    overflow: 'hidden',
    height: Dimensions.CARD_IMAGE_SIZE_SMALL,
    width: Dimensions.CARD_IMAGE_SIZE_SMALL,
    //backgroundColor: 'red',
  },
  circle: {},
  waveform: {
    width: 200,
  },
});
