import { View, StyleSheet, Image } from 'react-native';
import { useFonts } from 'expo-font';
import { PointerUp, PointerDown } from './Pointer';
import { BasicsLabel } from './BasicsLabel';
import { Colors } from '../../constants';

export const BasicsShared = props => {
  const [loaded] = useFonts({
    ObjektivBold: require('./../../../assets/fonts/ObjektivMk2-Bold.ttf'),
    ObjektivMedium: require('./../../../assets/fonts/ObjektivMk2-Medium.ttf'),
    ObjektivRegular: require('./../../../assets/fonts/ObjektivMk2-Regular.ttf'),
  });

  if (!loaded) {
    return null;
  }

  return (
    <>
      <View style={styles.container}>
        <View style={styles.backgroundImageWrapper}>
          <Image
            source={props.image}
            style={[styles.backgroundImage, { height: props.imageHeight }]}
            resizeMode="contain"
          />
        </View>
        <View style={styles.overlayWrapper}>
          <View style={styles.labelWrapper}>
            <BasicsLabel
              labelText={props.label1Text}
              animation={props.label1Animation}
              left={props.label1Horiz}
              delay={props.label1Delay}
            />
            <BasicsLabel
              labelText={props.label2Text}
              animation={props.label2Animation}
              left={props.label2Horiz}
              delay={props.label2Delay}
            />
          </View>
          <View style={styles.overlayTop}>
            {props.label1Text.length > 0 && (
              <PointerDown
                left={props.pointer1Horiz}
                vert={props.pointer1Vert}
                delay={props.label1Delay}
              />
            )}
            {props.label2Text.length > 0 && (
              <PointerDown
                left={props.pointer2Horiz}
                vert={props.pointer2Vert}
                delay={props.label2Delay}
              />
            )}
          </View>
          <View style={styles.overlayBottom}>
            {props.label3Text.length > 0 && (
              <PointerUp
                left={props.pointer3Horiz}
                vert={props.pointer3Vert}
                delay={props.label3Delay}
              />
            )}
            {props.label4Text.length > 0 && (
              <PointerUp
                left={props.pointer4Horiz}
                vert={props.pointer4Vert}
                delay={props.label4Delay}
              />
            )}
          </View>
          <View style={styles.labelWrapper}>
            <BasicsLabel
              labelText={props.label3Text}
              animation={props.label3Animation}
              left={props.label3Horiz}
              delay={props.label3Delay}
            />
            <BasicsLabel
              labelText={props.label4Text}
              animation={props.label4Animation}
              left={props.label4Horiz}
              delay={props.label4Delay}
            />
          </View>
        </View>
      </View>
    </>
  );
};

const LABEL_HEIGHT = 40;

const styles = StyleSheet.create({
  container: {
    display: 'flex',
    alignSelf: 'center',
    height: Dimensions.SETUP_HEIGHT,
    width: Dimensions.SETUP_WIDTH,
    backgroundColor: Colors.WHITE,
    //backgroundColor: 'blue',
  },
  backgroundImageWrapper: {
    position: 'relative',
    top: 0,
    left: 0,
    height: Dimensions.SETUP_HEIGHT,
    width: Dimensions.SETUP_WIDTH,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    //backgroundColor: 'green',
  },
  backgroundImage: {
    justifyContent: 'center',
    alignItems: 'center',
    alignContent: 'center',
    width: Dimensions.SETUP_WIDTH,
    //backgroundColor: 'gold',
  },
  overlayWrapper: {
    top: 0,
    left: 0,
    position: 'absolute',
    height: Dimensions.SETUP_HEIGHT,
    width: Dimensions.SETUP_WIDTH,
    //backgroundColor: 'green',
  },
  overlayTop: {
    height: Dimensions.SETUP_HEIGHT / 2 - LABEL_HEIGHT,
    overflow: 'hidden',
    width: Dimensions.SETUP_WIDTH,
    //backgroundColor: 'purple',
  },
  overlayBottom: {
    height: Dimensions.SETUP_HEIGHT / 2 - LABEL_HEIGHT,
    overflow: 'hidden',
    width: Dimensions.SETUP_WIDTH,
    //backgroundColor: 'blue',
  },
  labelWrapper: {
    justifyContent: 'center',
    height: LABEL_HEIGHT,
    //backgroundColor: 'blue',
  },
});
