import React from 'react';
import { View, Text, Pressable, StyleSheet } from 'react-native';
import { PressableOpacity } from 'react-native-pressable-opacity';
import { useNavigation } from '@react-navigation/native';
import { FontAwesome } from '@expo/vector-icons';

export const SharedCard = props => {
  const navigation = useNavigation();

  return (
    <>
      <View style={styles.cardWrapper}>
        <PressableOpacity
          onPress={() => navigation.navigate(props.navLink)}
          style={styles.cardInner}
        >
          <View style={styles.imageWrapper}>{props.image}</View>
          <View style={styles.descriptionWrapper}>
              <Text style={styles.title} numberOfLines={1} ellipsizeMode="tail">
                {props.title}
              </Text>
              <Text
                style={styles.description}
                numberOfLines={2}
                ellipsizeMode="tail"
              >
                {props.description}
              </Text>
            </View>
          <View style={styles.nextButtonWrapper}>
            <FontAwesome
              size={30}
              name="chevron-right"
              color={Colors.LOGO_BLUE}
              style={styles.nextButton}
            />
          </View>
        </PressableOpacity>
      </View>
    </>
  );
};

const styles = StyleSheet.create({
  cardWrapper: {
    height: Dimensions.CARD_HEIGHT,
    marginVertical: 5,
    width: '100%',
    paddingHorizontal: 10,
    //backgroundColor: 'darkblue',
  },
  cardInner: {
    height: Dimensions.CARD_HEIGHT,
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    paddingHorizontal: 15,
    borderRadius: 8,
    backgroundColor: Colors.WHITE,
    //backgroundColor: 'green',
  },
  imageWrapper: {
    height: Dimensions.CARD_IMAGE_SIZE_SMALL,
    width: Dimensions.CARD_IMAGE_SIZE_SMALL,
    //backgroundColor: '#ffcc00',
  },
  descriptionWrapper: {
    height: Dimensions.CARD_IMAGE_SIZE_SMALL,
    flexShrink: 1,
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    paddingHorizontal: 20,
    //backgroundColor: 'aqua',
  },
  title: {
    fontFamily: Fonts.FONT_FACE_TITLE,
    fontSize: Fonts.FONT_SIZE_DESCRIPTION,
    color: Colors.NAVY,
    lineHeight: Fonts.FONT_SIZE_DESCRIPTION * 1.5,
    //backgroundColor: 'red',
  },
  description: {
    fontFamily: Fonts.FONT_FACE_DESCRIPTION,
    fontSize: Fonts.FONT_SIZE_DESCRIPTION,
    color: Colors.NAVY,
    lineHeight: Fonts.FONT_SIZE_DESCRIPTION * 1.5,
    //backgroundColor: 'red',
  },
  nextButtonWrapper: {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    height: Dimensions.CARD_IMAGE_SIZE_SMALL,
    width: 20,
    //backgroundColor: '#ffcc00',
  },
});



