import { View, Text, StyleSheet } from 'react-native';
import * as Animatable from 'react-native-animatable';
import { Button } from './../Button';
import Leaves from '../../../assets/images/js/Leaves.js';

export const HeroDark = props => {
  return (
    <Animatable.View
      style={styles.container}
      animation="fadeIn"
      delay={0}
      duration={1000}
    >
      <Animatable.View
        style={styles.background}
        animation="fadeIn"
        delay={1500}
        duration={1500}
      >
        <Leaves style={styles.background} />
      </Animatable.View>
      <View style={styles.contentWrapper}>
        <Animatable.View
          animation="fadeInDown"
          delay={Animations.SPEED_MED}
          duration={Animations.SPEED_SLOW}
        >
          <Text style={styles.title}>{props.title}</Text>
        </Animatable.View>
        <Animatable.View
          animation="fadeIn"
          delay={Animations.SPEED_LONG}
          duration={Animations.SPEED_SLOW}
        >
          <Text style={styles.description}>{props.description}</Text>
        </Animatable.View>
        <Animatable.View
          animation="fadeInUp"
          delay={Animations.SPEED_XLONG}
          duration={Animations.SPEED_LONG}
        >
          <Button
            type="solid-green"
            buttonText={props.buttonText}
            onPress={() => props.navigation.navigate(props.link)}
          />
        </Animatable.View>
      </View>
    </Animatable.View>
  );
};

const styles = StyleSheet.create({
  container: {
    height: Dimensions.HERO_DARK_HEIGHT,
    backgroundColor: Colors.NAVY,
    width: '100%',
  },
  background: {
    position: 'absolute',
    height: Dimensions.HERO_DARK_HEIGHT,
    top: 0,
    left: 0,
    width: '100%',
  },
  contentWrapper: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: Dimensions.HERO_DARK_HEIGHT,
    paddingHorizontal: 20,
    //backgroundColor: 'red',
  },
  title: {
    fontFamily: Fonts.FONT_FACE_TITLE,
    fontSize: Fonts.FONT_SIZE_TITLE,
    color: Colors.WHITE,
    lineHeight: Fonts.FONT_SIZE_TITLE,
    //backgroundColor: '#ffcc00',
  },
  description: {
    marginVertical: 12,
    fontFamily: Fonts.FONT_FACE_DESCRIPTION,
    fontSize: Fonts.FONT_SIZE_DESCRIPTION,
    color: Colors.WHITE,
    textAlign: 'center',
    lineHeight: Fonts.FONT_SIZE_DESCRIPTION * 1.5,
    //backgroundColor: 'green',
  },
});
