import { View, Image } from 'react-native';
import * as Animatable from 'react-native-animatable';
import { Button } from '../../Button';
import { useFonts } from 'expo-font';
import Illustration_05 from '../../../../assets/images/js/Illustration_05';
import cardStyles from '../cardStyles';
const styles = cardStyles;

const description = 'Remember to do your tremor tasks before and after the therapy';
const buttonText = 'Watch How';
const link = 'Button';

export const CardTremorTasks = ({size}) => {
  const [loaded] = useFonts({
    ObjektivRegular: require('./../../../../assets/fonts/ObjektivMk2-Regular.ttf'),
    ObjektivBold: require('./../../../../assets/fonts/ObjektivMk2-Bold.ttf'),
  });

  if (!loaded) {
    return null;
  }
  return (
    <>
      {size === 'tall' ? (
        <View style={styles.cardWrapperTall}>
          <View style={styles.cardInner}>
            <Animatable.View
              animation="fadeIn"
              delay={Animations.SPEED_MED}
              duration={Animations.SPEED_XSLOW}
              style={styles.imageWrapper}
            >
              {/* <Image style={styles.image} source={image} /> */}
              <Illustration_05 style={styles.image} />
            </Animatable.View>
            <Animatable.Text
              animation="fadeInDown"
              delay={Animations.SPEED_FAST}
              duration={Animations.SPEED_FAST}
              style={styles.description}
            >
              {description}
            </Animatable.Text>
            <Animatable.View
              animation="fadeInUp"
              delay={Animations.SPEED_MED}
              duration={Animations.SPEED_FAST}
            >
              <Button buttonText={buttonText} type="outline" onPress={link} />
            </Animatable.View>
          </View>
        </View>
      ) : (
        <View style={styles.cardWrapperShort}>
          <View style={styles.cardInner}>
            <View style={styles.shortView}>
              <Animatable.View
                animation="fadeIn"
                delay={Animations.SPEED_MED}
                duration={Animations.SPEED_XSLOW}
                style={styles.imageWrapperShort}
              >
                {/* <Image style={styles.imageShort} source={image} /> */}
                <Illustration_05 style={styles.imageShort} />
              </Animatable.View>
              <Animatable.Text
                animation="fadeInDown"
                delay={Animations.SPEED_FAST}
                duration={Animations.SPEED_FAST}
                style={styles.descriptionShort}
              >
                {description}
              </Animatable.Text>
            </View>
            <Animatable.View
              animation="fadeInUp"
              delay={Animations.SPEED_MED}
              duration={Animations.SPEED_FAST}
            >
              <Button buttonText={buttonText} type="outline" onPress={link} />
            </Animatable.View>
          </View>
        </View>
      )}
    </>
  );
};
